import React, { useState } from 'react';
import axios from 'axios';

const AddBook = () => {
  const [title, setTitle] = useState('');

  const handleAddBook = async () => {
    try {
      const response = await axios.post('/api/books', { title });
      console.log(response.data); // Zwr�cona odpowied� od backendu
      // Opcjonalnie, mo�na zaktualizowa� list� ksi��ek po dodaniu nowej
      // poprzez wywo�anie odpowiedniej metody przekazanej przez props
    } catch (error) {
      console.error(error);
    }
  };

  return (
    <div>
      <h2>Add Book</h2>
      <input type="text" value={title} onChange={e => setTitle(e.target.value)} />
      <button onClick={handleAddBook}>Add</button>
    </div>
  );
};

export default AddBook;
