import React, { useState, useEffect } from 'react';
import axios from 'axios';

const Data_Admin = () => {
  const [books, setBooks] = useState([]);
  const [editingBook, setEditingBook] = useState(null);

  useEffect(() => {
    fetchBooks();
  }, []);

  const fetchBooks = async () => {
    const response = await axios.get('http://localhost:8000/api/books');
    setBooks(response.data);
  };

  const createBook = async () => {
    const author = await getOrCreateAuthor(editingBook.author.firstname, editingBook.author.lastname, editingBook.author.dateofbirth);

    if (!author) {
      console.log('Nie uda�o si� utworzy� autora.');
      return;
    }

    const existingBook = books.find((book) => book.title === editingBook.title);

    if (existingBook) {
      console.log('Ksi��ka o podanym tytule ju� istnieje.');
      return;
    }

    const newBook = {
      title: editingBook.title,
      author: author._id,
      price: editingBook.price,
    };

    await axios.post('http://localhost:8000/api/books', newBook);
    fetchBooks();
    setEditingBook(null);
  };

  const getOrCreateAuthor = async (firstname, lastname, dateofbirth) => {
    const response = await axios.get(`http://localhost:8000/api/authors?firstname=${firstname}&lastname=${lastname}`);

    if (response.data.length > 0) {
      return response.data[0];
    }

    const newAuthor = {
      firstname: firstname,
      lastname: lastname,
      dateofbirth: dateofbirth,
    };

    const createAuthorResponse = await axios.post('http://localhost:8000/api/authors', newAuthor);
    return createAuthorResponse.data;
  };

  const handleInputChange = (event) => {
    const { name, value } = event.target;
    setEditingBook((prevBook) => ({
      ...prevBook,
      [name]: value,
    }));
  };

  return (
    <div>
      <h1>Data Admin</h1>
      <h2>Books</h2>
      <table>
        <thead>
          <tr>
            <th>Title</th>
            <th>Author's Firstname</th>
            <th>Author's Lastname</th>
            <th>Author's Birth</th>
            <th>Price</th>
          </tr>
        </thead>
        <tbody>
          {books.map((book) => (
            <tr key={book._id}>
              <td>{book.title}</td>
              <td>{book.author.firstname}</td>
              <td>{book.author.lastname}</td>
              <td>{book.author.dateofbirth}</td>
              <td>{book.price}</td>
            </tr>
          ))}
        </tbody>
      </table>

      {editingBook ? (
        <div>
          <h3>Edit Book</h3>
          <label>Title:</label>
          <input type="text" name="title" value={editingBook.title} onChange={handleInputChange} />
          <label>Author's Firstname:</label>
          <input
            type="text"
            name="author.firstname"
            value={editingBook.author.firstname}
            onChange={handleInputChange}
          />
          <label>Author's Lastname:</label>
          <input
            type="text"
            name="author.lastname"
            value={editingBook.author.lastname}
            onChange={handleInputChange}
          />
          <label>Author's Birth:</label>
          <input
            type="text"
            name="author.dateofbirth"
            value={editingBook.author.dateofbirth}
            onChange={handleInputChange}
          />
          <label>Price:</label>
          <input type="text" name="price" value={editingBook.price} onChange={handleInputChange} />
          <button onClick={createBook}>Create Book</button>
        </div>
      ) : (
        <div>
          <h3>Add New Book</h3>
          <button onClick={() => setEditingBook({ title: '', author: { firstname: '', lastname: '', dateofbirth: '' }, price: '' })}>
            Open Form
          </button>
        </div>
      )}
    </div>
  );
};

export default Data_Admin;
