import React, { useEffect, useState } from 'react';
import axios from 'axios';
import './Books.css';

const Books = () => {
  const [books, setBooks] = useState([]);
  const [newBook, setNewBook] = useState({ title: '', author: '', price: 0, genre: '', stock: 0, publisher: '' });
  const [authors, setAuthors] = useState([]);
  const [editingBook, setEditingBook] = useState(null);
  const [errors, setErrors] = useState({});

  useEffect(() => {
    fetchBooks();
    fetchAuthors();
  }, []);

  const fetchBooks = async () => {
    try {
      const token = localStorage.getItem('token');
      const response = await axios.get('http://localhost:8000/api/books', {
        headers: {
          Authorization: `Bearer ${token}`,
        },
      });
      setBooks(response.data);
    } catch (error) {
      console.error(error);
    }
  };

  const fetchAuthors = async () => {
    try {
      const token = localStorage.getItem('token');
      const response = await axios.get('http://localhost:8000/api/authors', {
        headers: {
          Authorization: `Bearer ${token}`,
        },
      });
      setAuthors(response.data);
    } catch (error) {
      console.error(error);
    }
  };

  const handleInputChange = e => {
    if (editingBook) {
      setEditingBook({ ...editingBook, [e.target.name]: e.target.value });
    } else {
      setNewBook({ ...newBook, [e.target.name]: e.target.value });
    }
  };

  const handleAuthorSelect = e => {
    const selectedAuthor = authors.find(author => author._id === e.target.value);
    if (selectedAuthor) {
      if (editingBook) {
        setEditingBook({ ...editingBook, author: selectedAuthor });
      } else {
        setNewBook({ ...newBook, author: selectedAuthor });
      }
    }
  };

  const validateBook = () => {
    const errors = {};
    if (!newBook.title) {
      errors.title = 'Title is required';
    }
    if (!newBook.author) {
      errors.author = 'Author is required';
    }
    if (!newBook.price || newBook.price <= 0) {
      errors.price = 'Price must be a positive number';
    }
    if (!newBook.genre) {
      errors.genre = 'Genre is required';
    }
    if (!newBook.stock || newBook.stock < 0) {
      errors.stock = 'Stock must be a non-negative number';
    }
    if (!newBook.publisher) {
      errors.publisher = 'Publisher is required';
    }
    setErrors(errors);
    return Object.keys(errors).length === 0;
  };

  const addBook = () => {
    if (validateBook()) {
      axios.post('http://localhost:8000/api/books', newBook)
        .then(response => {
          setNewBook({ title: '', author: '', price: 0, genre: '', stock: 0, publisher: '' });
          fetchBooks();
        })
        .catch(error => {
          console.error(error);
        });
    }
  };

  const editBook = (book) => {
    setEditingBook(book);
  };

  const updateBook = () => {
    axios.put(`http://localhost:8000/api/books/${editingBook._id}`, editingBook)
      .then(response => {
        setEditingBook(null);
        fetchBooks();
      })
      .catch(error => {
        console.error(error);
      });
  };

  const deleteBook = (id) => {
    axios.delete(`http://localhost:8000/api/books/${id}`)
      .then(response => {
        fetchBooks();
      })
      .catch(error => {
        console.error(error);
      });
  };
    return (
    <div className="books-container">
      <h1>Books</h1>

      <div className="add-book-form">
               <h2>Add Book</h2>
        {/* Title Input */}
        <input
          type="text"
          name="title"
          value={newBook.title}
          placeholder="Title"
          onChange={handleInputChange}
        />
        {errors.title && <span className="error">{errors.title}</span>}
        
        {/* Author Select */}
        <select name="author" value={newBook.author} onChange={handleAuthorSelect}>
          <option value="">Select Author</option>
          {authors.map(author => (
            <option key={author._id} value={author._id}>{author.firstname} {author.lastname}</option>
          ))}
        </select>
        {errors.author && <span className="error">{errors.author}</span>}

        {/* Price Input */}
        <input
          type="number"
          name="price"
          value={newBook.price}
          placeholder="Price"
          onChange={handleInputChange}
        />
        {errors.price && <span className="error">{errors.price}</span>}

        {/* Genre Select */}
        <select name="genre" value={newBook.genre} onChange={handleInputChange}>
          <option value="">Select Genre</option>
          <option value="Fantasy">Fantasy</option>
          <option value="Science Fiction">Science Fiction</option>
          <option value="Mystery">Mystery</option>
          <option value="Romance">Romance</option>
          <option value="Thriller">Thriller</option>
          <option value="Epopeja">Epopeja</option>
          <option value="Wiersz">Wiersz</option>
          <option value="Poradnik">Poradnik</option>
        </select>
        {errors.genre && <span className="error">{errors.genre}</span>}

        {/* Stock Input */}
        <input
          type="number"
          name="stock"
          value={newBook.stock}
          placeholder="Stock"
          onChange={handleInputChange}
        />
        {errors.stock && <span className="error">{errors.stock}</span>}

        {/* Publisher Input */}
        <input
          type="text"
          name="publisher"
          value={newBook.publisher}
          placeholder="Publisher"
          onChange={handleInputChange}
        />
        {errors.publisher && <span className="error">{errors.publisher}</span>}

        <button onClick={addBook}>Add</button>
      </div>

      <h2>Books List</h2>
      <ul className="books-list">
        {books.map(book => (
          <li key={book._id} className="book-item">
            <strong>Title:</strong> {book.title}<br />
            <strong>Author:</strong> {book.author.firstname} {book.author.lastname}<br />
            <strong>Price:</strong> {book.price}<br />
            <strong>Genre:</strong> {book.genre}<br />
            <strong>Stock:</strong> {book.stock}<br />
            <strong>Publisher:</strong> {book.publisher}<br />

            {editingBook && editingBook._id === book._id ? (
              <div className="edit-book-form">
                <input
                  type="text"
                  name="title"
                  value={editingBook.title}
                  onChange={handleInputChange}
                />
                <select name="author" value={editingBook.author._id} onChange={handleAuthorSelect}>
                  <option value="">Select Author</option>
                  {authors.map(author => (
                    <option key={author._id} value={author._id}>{author.firstname} {author.lastname}</option>
                  ))}
                </select>
                <input
                  type="number"
                  name="price"
                  value={editingBook.price}
                  onChange={handleInputChange}
                />
                <select name="genre" value={editingBook.genre} onChange={handleInputChange}>
                  <option value="">Select Genre</option>
                  <option value="Fantasy">Fantasy</option>
                  <option value="Science Fiction">Science Fiction</option>
                  <option value="Mystery">Mystery</option>
                  <option value="Romance">Romance</option>
                  <option value="Thriller">Thriller</option>
                  <option value="Epopeja">Epopeja</option>
                  <option value="Wiersz">Wiersz</option>
                  <option value="Poradnik">Poradnik</option>
                </select>
                <input
                  type="number"
                  name="stock"
                  value={editingBook.stock}
                  onChange={handleInputChange}
                />
                <input
                  type="text"
                  name="publisher"
                  value={editingBook.publisher}
                  onChange={handleInputChange}
                />
                <button onClick={updateBook}>Save</button>
                <button onClick={() => setEditingBook(null)}>Cancel</button>
              </div>
            ) : (
              <div className="book-buttons">
                <button onClick={() => editBook(book)}>Edit</button>
                <button onClick={() => deleteBook(book._id)}>Delete</button>
              </div>
            )}

            <hr />
          </li>
        ))}
      </ul>
    </div>
  );
};

export default Books;
