import React from 'react';
import { BrowserRouter as Router, Link, Route, Routes } from 'react-router-dom';
import Authors from './Authors';
import Books from './Books';
import Home from './Home';
import RegisterForm from './Register';
import LoginForm from './Login';
import './App.css';

const App = () => {
  return (
    <Router>
      <div className="container">
        <nav className="navbar">
          <Link to="/">Home</Link>
          <Link to="/authors">Authors</Link>
          <Link to="/books">Books</Link>
        </nav>

        <Routes>
          <Route path="/" element={<Home />} />
          <Route path="/authors" element={<Authors />} />
          <Route path="/books" element={<Books />} />
          <Route path="/authors/:id" element={<AuthorDetails />} /> {/* Dodany widok szczeg�owy autora */}
          <Route path="/register" element={<RegisterForm />} />
          <Route path="/login" element={<LoginForm />} />
        </Routes>
      </div>
    </Router>
  );
};

// Dodany komponent AuthorDetails
const AuthorDetails = ({ match }) => {
  const { id } = match.params;
  // Tutaj umie�� kod pobieraj�cy dane autora o podanym id i wy�wietlaj�cy je

  return <div>Author Details Page - ID: {id}</div>;
};

export default App;
