import React, { useState } from 'react';
import axios from 'axios';
import './Login.css'; // Importuj plik CSS

const Login = () => {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');

  const handleSubmit = async (e) => {
    e.preventDefault();

    try {
      const response = await axios.post('http://localhost:8000/api/login', {
        username,
        password
      });
      console.log(response.data);
      // Mo�esz doda� tutaj kod do obs�ugi sukcesu logowania
    } catch (error) {
      console.error(error);
      // Mo�esz doda� tutaj kod do obs�ugi b��d�w logowania
    }
  };

  return (
    <div className="login-container"> {/* Dodaj klas� kontenera */}
      <h2>Logowanie</h2>
      <form onSubmit={handleSubmit}>
        <input
          type="text"
          placeholder="Nazwa u�ytkownika"
          value={username}
          onChange={(e) => setUsername(e.target.value)}
        />
        <input
          type="password"
          placeholder="Has�o"
          value={password}
          onChange={(e) => setPassword(e.target.value)}
        />
        <button type="submit">Zaloguj</button>
      </form>
    </div>
  );
};

export default Login;
