import React, { useState, useEffect } from 'react';
import axios from 'axios';
import AddBook from './AddBook';
import EditBook from './EditBook';
import Book from './Book';

const BookList = () => {
  const [books, setBooks] = useState([]);
  const [successMessage, setSuccessMessage] = useState('');

  useEffect(() => {
    fetchBooks();
  }, []);

  const fetchBooks = async () => {
    try {
      const response = await axios.get('http://localhost:8000/api/books');
      setBooks(response.data);
    } catch (error) {
      console.error(error);
    }
  };

  const handleAddBook = async (title) => {
    try {
      const response = await axios.post('/api/books', { title });
      setSuccessMessage('Book added successfully');
      setBooks([...books, response.data]);
    } catch (error) {
      console.error(error);
    }
  };

  const handleEditBook = async (id, title) => {
    try {
      const response = await axios.put(`/api/books/${id}`, { title });
      setSuccessMessage('Book updated successfully');
      const updatedBooks = books.map(book =>
        book.id === id ? { ...book, title: response.data.title } : book
      );
      setBooks(updatedBooks);
    } catch (error) {
      console.error(error);
    }
  };

  const handleDeleteBook = async (id) => {
    try {
      await axios.delete(`/api/books/${id}`);
      setSuccessMessage('Book deleted successfully');
      const updatedBooks = books.filter(book => book.id !== id);
      setBooks(updatedBooks);
    } catch (error) {
      console.error(error);
    }
  };

  return (
    <div>
      <h1>Book List</h1>

      {successMessage && <p>{successMessage}</p>}

      <AddBook onAddBook={handleAddBook} />

      <table>
        <thead>
          <tr>
            <th>Title</th>
            <th>Actions</th>
          </tr>
        </thead>
        <tbody>
          {books.map(book => (
            <tr key={book.id}>
              <td>{book.title}</td>
              <td>
                <EditBook book={book} onEditBook={handleEditBook} />
                <button onClick={() => handleDeleteBook(book.id)}>Delete</button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default BookList;
