import React from 'react';
import axios from 'axios';

const DataAdmin = () => {
  const [data, setData] = React.useState([]);

  React.useEffect(() => {
    fetchData();
  }, []);

  const fetchData = () => {
    axios
      .get('http://localhost:8000/api/data')
      .then(response => {
        setData(response.data);
      })
      .catch(error => {
        console.error(error);
      });
  };

  const handleInputChange = (e, index, field) => {
    const newData = [...data];
    newData[index][field] = e.target.value;
    setData(newData);
  };

  const updateData = (id, newData) => {
    axios
      .put(`http://localhost:8000/api/data/${id}`, newData)
      .then(response => {
        fetchData();
      })
      .catch(error => {
        console.error(error);
      });
  };

  return (
    <div>
      <h1>Data Admin</h1>
      <table>
        <thead>
          <tr>
            <th>Title</th>
            <th>Author's First Name</th>
            <th>Author's Last Name</th>
            <th>Author's Birth</th>
            <th>Price</th>
            <th>Action</th>
          </tr>
        </thead>
        <tbody>
          {data.map((item, index) => (
            <tr key={item._id}>
              <td>
                <input
                  type="text"
                  value={item.title}
                  onChange={e => handleInputChange(e, index, 'title')}
                />
              </td>
              <td>
                <input
                  type="text"
                  value={item.author.firstname}
                  onChange={e => handleInputChange(e, index, 'author.firstname')}
                />
              </td>
              <td>
                <input
                  type="text"
                  value={item.author.lastname}
                  onChange={e => handleInputChange(e, index, 'author.lastname')}
                />
              </td>
              <td>
                <input
                  type="text"
                  value={item.author.dateofbirth}
                  onChange={e => handleInputChange(e, index, 'author.dateofbirth')}
                />
              </td>
              <td>
                <input
                  type="text"
                  value={item.price}
                  onChange={e => handleInputChange(e, index, 'price')}
                />
              </td>
              <td>
                <button onClick={() => updateData(item._id, item)}>Save</button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default DataAdmin;
