import React, { useEffect, useState } from 'react';
import axios from 'axios';

const Data_Admin = () => {
  const [data, setData] = useState([]);
  const [editingData, setEditingData] = useState(null);
  const [newData, setNewData] = useState({ title: '', author: '', price: '' });
  const [authors, setAuthors] = useState([]);

  useEffect(() => {
    fetchAuthors();
    fetchData();
  }, []);

  const fetchAuthors = () => {
    axios.get('http://localhost:8000/api/authors')
      .then(response => {
        setAuthors(response.data);
      })
      .catch(error => {
        console.error(error);
      });
  };

  const fetchData = () => {
    axios.get('http://localhost:8000/api/books')
      .then(response => {
        setData(response.data);
      })
      .catch(error => {
        console.error(error);
      });
  };

  const handleInputChange = e => {
    if (editingData) {
      setEditingData({ ...editingData, [e.target.name]: e.target.value });
    } else {
      setNewData({ ...newData, [e.target.name]: e.target.value });
    }
  };

  const addData = () => {
    axios.post('http://localhost:8000/api/books', newData)
      .then(response => {
        setNewData({ title: '', author: '', price: '' });
        fetchData();
      })
      .catch(error => {
        console.error(error);
      });
  };

  const updateData = () => {
    if (editingData) {
      axios.put(`http://localhost:8000/api/books/${editingData._id}`, editingData)
        .then(response => {
          setEditingData(null);
          fetchData();
        })
        .catch(error => {
          console.error(error);
        });
    }
  };

  return (
    <div>
      <h1>Data Admin</h1>

      <h2>Add Data</h2>
      <input
        type="text"
        name="title"
        value={newData.title}
        placeholder="Title"
        onChange={handleInputChange}
      />
      <select
        name="author"
        value={newData.author}
        onChange={handleInputChange}
      >
        <option value="">Select Author</option>
        {authors.map(author => (
          <option key={author._id} value={author._id}>
            {author.firstname} {author.lastname}
          </option>
        ))}
      </select>
      <input
        type="text"
        name="price"
        value={newData.price}
        placeholder="Price"
        onChange={handleInputChange}
      />
      <button onClick={addData}>Add</button>

      <h2>Data List</h2>
      <table>
        <thead>
          <tr>
            <th>Title</th>
            <th>Author's Firstname</th>
            <th>Author's Lastname</th>
            <th>Author's Birth</th>
            <th>Price</th>
            <th>Actions</th>
          </tr>
        </thead>
        <tbody>
          {data.map(item => (
            <tr key={item._id}>
              <td>{item.title}</td>
              <td>{item.author.firstname}</td>
              <td>{item.author.lastname}</td>
              <td>{item.author.dateofbirth}</td>
              <td>{item.price}</td>
              <td>
                {editingData && editingData._id === item._id ? (
                  <>
                    <input
                      type="text"
                      name="title"
                      value={editingData.title}
                      onChange={handleInputChange}
                    />
                    <input
                      type="text"
                      name="price"
                      value={editingData.price}
                      onChange={handleInputChange}
                    />
                    <button onClick={updateData}>Save</button>
                    <button onClick={() => setEditingData(null)}>Cancel</button>
                  </>
                ) : (
                  <button onClick={() => setEditingData(item)}>Edit</button>
                )}
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default Data_Admin;
