import React, { useEffect, useState } from 'react';
import axios from 'axios';

const Books = () => {
  const [books, setBooks] = useState([]);
  const [newBook, setNewBook] = useState({
    title: '',
    author: '',
    price: 0
  });

  useEffect(() => {
    fetchBooks();
  }, []);

  const fetchBooks = () => {
    axios.get('http://localhost:8000/api/books')
      .then(response => {
        setBooks(response.data[0].books);
      })
      .catch(error => {
        console.error(error);
      });
  };

  const handleInputChange = e => {
    setNewBook({ ...newBook, [e.target.name]: e.target.value });
  };

  const handleAddBook = () => {
    axios.post('http://localhost:8000/api/books', newBook)
      .then(response => {
        console.log(response.data);
        setNewBook({ title: '', author: '', price: 0 });
        fetchBooks(); // Zaktualizuj list� ksi��ek po dodaniu
      })
      .catch(error => {
        console.error(error);
      });
  };

  const handleDeleteBook = bookId => {
    axios.delete(`http://localhost:8000/api/books/${bookId}`)
      .then(response => {
        console.log(response.data);
        fetchBooks(); // Zaktualizuj list� ksi��ek po usuni�ciu
      })
      .catch(error => {
        console.error(error);
      });
  };

  return (
    <div>
      <h1>Books</h1>
      <ul>
        {books.map(book => (
          <li key={book._id}>
            {book.title} by {book.author} - ${book.price}
            <button onClick={() => handleDeleteBook(book._id)}>Delete</button>
          </li>
        ))}
      </ul>

      <h2>Add New Book</h2>
      <div>
        <input
          type="text"
          name="title"
          placeholder="Title"
          value={newBook.title}
          onChange={handleInputChange}
        />
        <input
          type="text"
          name="author"
          placeholder="Author"
          value={newBook.author}
          onChange={handleInputChange}
        />
        <input
          type="number"
          name="price"
          placeholder="Price"
          value={newBook.price}
          onChange={handleInputChange}
        />
        <button onClick={handleAddBook}>Add Book</button>
      </div>
    </div>
  );
};

export default Books;
