import React, { useState } from 'react';
import { BrowserRouter as Router, Link, Route, Routes } from 'react-router-dom';
import axios from 'axios';
import Authors from './Authors';
import Books from './Books';
import Home from './Home';
import './App.css';

const App = () => {
  const [isAuthenticated, setIsAuthenticated] = useState(false);

  const handleRegister = async () => {
    try {
      const response = await axios.post('http://localhost:8000/api/register', {
        name: 'John Doe',
        email: 'john.doe@example.com',
        password: 'password',
      });
      if (response.status === 200) {
        console.log('Rejestracja zako�czona sukcesem.');
        setIsAuthenticated(true);
      }
    } catch (error) {
      console.error('B��d podczas rejestracji:', error.response.data);
    }
  };

  const handleLogin = async () => {
    try {
      const response = await axios.post('http://localhost:8000/api/login', {
        email: 'john.doe@example.com',
        password: 'password',
      });
      if (response.status === 200) {
        console.log('Logowanie zako�czone sukcesem.');
        setIsAuthenticated(true);
      }
    } catch (error) {
      console.error('B��d podczas logowania:', error.response.data);
    }
  };

  return (
    <Router>
      <div className="container">
        <nav className="navbar">
          <Link to="/">Home</Link>
          <Link to="/authors">Authors</Link>
          <Link to="/books">Books</Link>
        </nav>

        {!isAuthenticated && (
          <div className="auth-buttons">
            <button onClick={handleRegister}>Rejestracja</button>
            <button onClick={handleLogin}>Logowanie</button>
          </div>
        )}

        <Routes>
          <Route path="/" element={<Home />} />
          <Route path="/authors" element={<Authors />} />
          <Route path="/books" element={<Books />} />
        </Routes>
      </div>
    </Router>
  );
};

export default App;
