import React, { useEffect, useState } from 'react';
import axios from 'axios';

const Books = () => {
  const [books, setBooks] = useState([]);
  const [newBook, setNewBook] = useState({ title: '', author: '', price: '' });
  const [editingBook, setEditingBook] = useState(null);

  useEffect(() => {
    fetchBooks();
  }, []);

  const fetchBooks = () => {
    axios.get('http://localhost:8000/api/books')
      .then(response => {
        setBooks(response.data);
      })
      .catch(error => {
        console.error(error);
      });
  };

  const handleInputChange = e => {
    setNewBook({ ...newBook, [e.target.name]: e.target.value });
  };

  const addBook = () => {
    axios.post('http://localhost:8000/api/books', newBook)
      .then(response => {
        setNewBook({ title: '', author: '', price: '' });
        fetchBooks();
      })
      .catch(error => {
        console.error(error);
      });
  };

  const startEditing = (book) => {
    setEditingBook({ ...book });
  };

  const cancelEditing = () => {
    setEditingBook(null);
  };

  const updateBook = () => {
    axios.put(`http://localhost:8000/api/books/${editingBook._id}`, editingBook)
      .then(response => {
        setEditingBook(null);
        fetchBooks();
      })
      .catch(error => {
        console.error(error);
      });
  };

  const deleteBook = (id) => {
    axios.delete(`http://localhost:8000/api/books/${id}`)
      .then(response => {
        fetchBooks();
      })
      .catch(error => {
        console.error(error);
      });
  };

  return (
    <div>
      <h1>Books</h1>

      <h2>Add Book</h2>
      <input
        type="text"
        name="title"
        value={newBook.title}
        placeholder="Title"
        onChange={handleInputChange}
      />
      <input
        type="text"
        name="author"
        value={newBook.author}
        placeholder="Author"
        onChange={handleInputChange}
      />
      <input
        type="text"
        name="price"
        value={newBook.price}
        placeholder="Price"
        onChange={handleInputChange}
      />
      <button onClick={addBook}>Add</button>

      <h2>Books List</h2>
      <ul>
        {books.map(book => (
          <li key={book._id}>
            <strong>Title:</strong> {book.title}<br />
            <strong>Author:</strong> {book.author}<br />
            <strong>Price:</strong> {book.price}<br />

            {editingBook && editingBook._id === book._id ? (
              <>
                <button onClick={updateBook}>Save</button>
                <button onClick={cancelEditing}>Cancel</button>
              </>
            ) : (
              <>
                <button onClick={() => startEditing(book)}>Edit</button>
                <button onClick={() => deleteBook(book._id)}>Delete</button>
              </>
            )}
            <hr />
          </li>
        ))}
      </ul>
    </div>
  );
};

export default Books;
